import { getCompanyByIdService } from "../services/CompanyService";
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";
// import {getCompanyByIdService,getAllCompanyService,addCompanyService,updateCompanyService,deleteCompanyService } from "../services/CompanyService";
// import { getEmpById, getAllEmps } from '../redux/EmpSlice';
import axios from "axios";

import { getcompanyById,getAllCompany,deletecompanyById } from "../../redux/CompanySlice";
import Company from "../models/Company";

const CompanyData = () => {


    const [newCompanyObj, setNewCompanyObj] = useState(new Company());
    const [updtCompanyObj, setUpdtCompanyObj] = useState(new Company());
    const [displayCompanyObj, setDisplayCompanyObj] = useState(new Company());
    const [updateCompanyObj, setUpdateCompanyObj] = useState(new Company());
    const [companyid, setcompanyid] = useState('');
    const [deleteCompany, setDeleteCompany] = useState('');
    const dispatch = useDispatch();
    const companyDataFromStore = useSelector((state) => state.company.companyState);
    const companyList = useSelector((state) => state.company.companyList);
    const companyDelete = useSelector((state) => state.company.companyDelete);

    const handleCompany = (e) => {
        console.log('handlecompany');
        setcompanyid(e.target.value);
    }

    const handleAddCompany = (e) => {
        console.log(e.target.value);
        setNewCompanyObj({
            ...newCompanyObj,
            [e.target.name]: e.target.value,
        });
    }
    const handleUpdateCompany = (e) => {
        console.log(e.target.value);
        setUpdtCompanyObj({
            ...updteCompanyObj,
            [e.target.name]: e.target.value,
        });
    }

    const handleDeleteCompany = (ev) => {
        console.log('handleDeleteCompany');
        setDeleteCompany(ev.target.value);
    }

    const submitGetCompanyById = (evt) => {
        evt.preventDefault();
        console.log('submitGetCompanyById');
        getCompanyByIdService(fid)
            .then((response) => { dispatch(getcompanyById(response.data)) })
            .catch(() => {
                alert(`company with ${companyid} not found.`);
            });
        console.log(Object.keys(companyList));
        setcompanyid('');
    }

    const submitGetAllCompany = (evt) => {
        evt.preventDefault();
        console.log('submitGetAllCompany');
        getAllCompanyService()
            .then((response) => {
                dispatch(getAllCompany(response.data));
            })
            .catch(() => {
                alert(`Something is wrong!`);
            });
    }

    const submitAddCompany = (evt) => {
        evt.preventDefault();
        console.log('addFCompany');
        axios.post(`http://localhost:8082/company/add`, newCompanyObj)
            .then((response) => {
                setDisplayCompanyObj(response.data);
                alert('company added successfully.');
                setNewCompanyObj({ companytName:'', address:'',mobileNumber:'',email:'',password:''})
            
            })
            .catch(() => {
                alert("Company could not be added.");
            });
    }

    const submitUpdateCompany = (evt) => {
        evt.preventDefault();
        console.log('addCompany');
        axios.put(`http://localhost:8082/company/update`, updtCompanyObj)
            .then((response) => {
                setUpdateCompanyObj(response.data);
                alert('Company details updated successfully.');
                setNewCompanyObj({ companyName:'', address:'',mobileNumber:'',email:'',password:''})
            
            })
            .catch(() => {
                alert("Company could not be found.");
            });
    }

    const submitDeleteCompany = (evt) => {
        evt.preventDefault();
        console.log('deleteCompanyDetails');
        axios.delete(`http://localhost:8082/company/delete/${companyid}`)///delete/{companyid
            .then((response) => {
                alert(`Company details deleted successfully.`)
                // dispatch(deletecompanyById(response.data));    //deletecompanyById         // Sending data to redux store

            })
            .catch(() => {
                alert(`company not found.`);
            });

    }

    return (
        <div>
            <h1 className="display-4 text-primary mt-3 mb-3" >Company Component</h1>

            <div className="col-4 border border-light shadow p-3 mb-5 bg-white">
                <p>Find Company by id</p>
                <form className="form form-group form-primary" onSubmit={submitGetCompanyById}>
                    <input className="form-control mt-3" type="number" id="companyId" name="companyId" value={fid} onChange={handleCompany} placeholder="Enter company id to search" autoFocus required />
                    <input className="form-control mt-3 btn btn-primary" type="submit" value="Search company" />
                </form>
                <p>Company details: {companyDataFromStore.companyId} {companyDataFromStore.companyName} {companyDataFromStore.email} {companyDataFromStore.password}{companyDataFromStore.mobileNumber}{companyDataFromStore.address}</p>
            </div>

            <div>
                <div className="col-6 border border-light shadow p-3 mb-5 bg-white">
                    <p>List of all Company</p>
                    <div>
                        <form className="form form-group form-primary">
                            <input className="mt-3 btn btn-primary btn-block" type="button" onClick={submitGetAllCompany} value="Find All Comapnies" />
                        </form>
                    </div >
                    <table className="table table-light table-striped ">
                        <thead>
                            <tr>
                                <th>companyId</th>
                                <th>companyName</th>
                                <th>email</th>
                                 <th>password</th>
                                <th>mobileNumber</th>
                                <th>address</th>
                            </tr>
                        </thead>
                        <tbody>
                            {companyList.map((company, k) => {
                                return (
                                    <tr k={k}> <td>{company.companyId}</td> <td>{company.companyName}</td><td>{company.email}</td><td>{company.password}</td><td>{company.mobileNumber}</td><td>{company.address}</td></tr>
                                )
                            })}
                        </tbody>
                    </table>
                </div>
            </div>

            <div className="col-4 border border-light shadow p-3 mb-5 bg-white">
            
                <p>Add New company</p>
                {/* <form onSubmit={submitAddEmp}> */}
